#================================================================================= # Describe Script Here # # Author: Your Name # v2.1 # https://kevinholman.com/2017/09/10/demo-scom-script-template/ #================================================================================= param($SourceId,$ManagedEntityId,[string]$ComputerName,[string]$DebugLogging)
# Manual Testing section - put stuff here for manually testing script - typically parameters: #================================================================================= # $SourceId = '{00000000-0000-0000-0000-000000000000}' # $ManagedEntityId = '{00000000-0000-0000-0000-000000000000}' # $ComputerName = "system.domain.com" # $DebugLogging = "true" #=================================================================================
# Constants section - modify stuff here: #================================================================================= # Assign script name variable for use in event logging. # ScriptName should be the same as the ID of the module that the script is contained in $ScriptName = "GetBackupjobs.ps1" $EventID = "3201" #=================================================================================
# Starting Script section - All scripts get this #================================================================================= # Gather the start time of the script $StartTime = Get-Date #Set variable to be used in logging events $whoami = whoami # Load MOMScript API $momapi = New-Object -comObject MOM.ScriptAPI #Log script event that we are starting task $momapi.LogScriptEvent($ScriptName,$EventID,0,"`nScript is starting. `nRunning as ($whoami).") #=================================================================================
#============================================ # Functions #============================================ function GetJobSchedule($schXml) { #complete $dailyNode = $schXml.JobScheduleOptionsInfo.OptionsDaily $monthlyNode = $schXml.JobScheduleOptionsInfo.OptionsMonthly $periodicallyNode = $schXml.JobScheduleOptionsInfo.OptionsPeriodically $continuousNode = $schXml.JobScheduleOptionsInfo.OptionsContinuous $chainingNode = $schXml.JobScheduleOptionsInfo.OptionsChaining $schTxt = "" if ($dailyNode.Enabled -eq "true") { $daysTxt = ($dailyNode.Days).split(",").trim().substring(0,3) -join ", " ($date,$time) = $dailyNode.Time.split("T") $schTxt += "Every " + $daysTxt + " at " + $time } elseif ($monthlyNode.Enabled -eq "true") { $monthTxt =($monthlyNode.Months).split(",").trim().substring(0,3) -join ", " $schTxt += "Every " + $monthTxt
$dNum = $monthlyNode.DayNumberInMonth.ToLower() $dName = $monthlyNode.DayOfWeek $schTxt += " on " + $dNum + " " + $dName; ($date,$time)=$monthlyNode.Time.Split("T") $schTxt += " at " + $time } elseif ($periodicallyNode.Enabled -eq "true") { $kind = $periodicallyNode.Kind $fullPeriod = $periodicallyNode.FullPeriod if ($kind -eq "Hours") { $fullPeriod = $fullPeriod / 60; } $schTxt += "Every " + $fullPeriod + " " + $kind; } elseif ($continuousNode.enabled -ne "false") { $schTxt += "Continuous"; } elseif ($chainingNode.Enabled -eq "true") { $jobId = $chainingNode.PreviousJobId schTxt += "After [" + $jobId + "]"; } return $schTxt }
# Discovery Script section - Discovery scripts get this #================================================================================= # Load SCOM Discovery module $DiscoveryData = $momapi.CreateDiscoveryData(0,$SourceId,$ManagedEntityId) #=================================================================================
# Begin MAIN script section #================================================================================= # Gather the jobs from the backup server $targetComputer $nameSpace = "root\veeamBS"
# Vault password retrieval $userName = "username" $pw = "password" $securePw = $pw | ConvertTo-SecureString -AsPlainText -Force [pscredential]$mc = New-Object System.Management.Automation.PSCredential ($userName, $securePw)
#Test the credentials $Error.Clear() invoke-command -scriptblock {Get-WmiObject -Namespace "root\VeeamBS" -ClassName wmi_extension} -ComputerName "$ComputerName" #-Credential $mc if ($Error.Count -gt 0) { $momapi.LogScriptEvent($ScriptName,$EventID,1,"`nError count is $($Error.Count).`n Failed Password retrieval`n URI: $uri`n$p`n Error Message: $error.ErrorDetails.Message") return 1 }
Set-Variable -name Jobs -value (invoke-command -scriptblock {Get-WmiObject -Namespace "root\VeeamBS" -ClassName job } -ComputerName "$ComputerName") #-Credential $mc) Set-Variable -name Repositories -value (invoke-command -scriptblock {Get-WmiObject -Namespace "root\VeeamBS" -ClassName RepositoryBase} -ComputerName "$ComputerName") #-Credential $mc) $JobsByType = ($Jobs | ?{$_.Type -eq "0"}) # Type 0 - Backup Job $Count = $JobsByType.Count IF ($DebugLogging.ToUpper() -eq "TRUE") {$momapi.LogScriptEvent($ScriptName,$EventID,0,"`nJob count is $Count")}
foreach ($job in $JobsByType ) { $jobName=$job.name $type="Backup Job" # $job.Type $enabled=$scheduleEnabled=$job.ScheduleEnabled $instanceUid=$job.InstanceUid $platform=$job.Platform $AppAwareProcessingEnabled=$job.AppAwareProcessingEnabled $repositoryID=$job.RepositoryId $repositoryName= ($repositories | ?{$_.InstanceUid -eq "$RepositoryID"}).Name $retention=$job.retainDays $VmwareToolsQuiesceEnabled=$job.VmwareToolsQuiesceEnabled $schedule=(GetJobSchedule $job.ScheduleOptions) IF ($DebugLogging.ToUpper() -eq "TRUE") {$momapi.LogScriptEvent($ScriptName,$EventID,0,"$type $jobName $enabled $platform $scheduleEnabled $instanceUid $AppAwareProcessingEnabled $repositoryId $retention $VmwareToolsQuiesceEnabled $schedule")}
Set-Variable -name repoName -value ( Invoke-command -scriptblock {param($rid); Get-WmiObject -Namespace "root\VeeamBS" -ClassName "repository" -Filter "InstanceUid='$rid'"} -ArgumentList $job.RepositoryId -ComputerName $ComputerName) #-Credential $mc)
# Discovery Script section - Discovery scripts get this $instance = $DiscoveryData.CreateClassInstance("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']$") $instance.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $ComputerName) $instance.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", $jobName) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/BackupServer$", $ComputerName) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Enabled$", $Enabled) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/InstanceUid$", $instanceUid) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Platform$", $platform) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Schedule$", $schedule) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/ScheduleEnabled$",$scheduleEnabled) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Type$", $type) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/AppAwareProcessingEnabled$", $AppAwareProcessingEnabled) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Repository$", $repositoryName) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/Retention$", $retention) $instance.AddProperty("$MPElement[Name='Custom.Veeam.BackupJobs.LocalApplication.Hosted.Class']/VmwareToolsQuiesceEnabled$", $VmwareToolsQuiesceEnabled) $DiscoveryData.AddInstance($instance) }
# Return Discovery Items Normally $DiscoveryData # Return Discovery Bag to the command line for testing (does not work from ISE) # $momapi.Return($DiscoveryData) #================================================================================= # End MAIN script section
# End of script section #================================================================================= #Log an event for script ending and total execution time. $EndTime = Get-Date $ScriptTime = ($EndTime - $StartTime).TotalSeconds $momapi.LogScriptEvent($ScriptName,$EventID,0,"`nScript Completed. `nRuntime: ($ScriptTime) seconds.") #================================================================================= # End of script

