#============================================ # Functions #============================================ function GetJobSchedule($schXml) { #complete $dailyNode = $schXml.JobScheduleOptionsInfo.OptionsDaily $monthlyNode = $schXml.JobScheduleOptionsInfo.OptionsMonthly $periodicallyNode = $schXml.JobScheduleOptionsInfo.OptionsPeriodically $continuousNode = $schXml.JobScheduleOptionsInfo.OptionsContinuous $chainingNode = $schXml.JobScheduleOptionsInfo.OptionsChaining $schTxt = "" if ($dailyNode.Enabled -eq "true") { $daysTxt = ($dailyNode.Days).split(",").trim().substring(0,3) -join ", " ($date,$time) = $dailyNode.Time.split("T") $schTxt += "Every " + $daysTxt + " at " + $time } elseif ($monthlyNode.Enabled -eq "true") { $monthTxt =($monthlyNode.Months).split(",").trim().substring(0,3) -join ", " $schTxt += "Every " + $monthTxt $dNum = $monthlyNode.DayNumberInMonth.ToLower() $dName = $monthlyNode.DayOfWeek $schTxt += " on " + $dNum + " " + $dName; ($date,$time)=$monthlyNode.Time.Split("T") $schTxt += " at " + $time } elseif ($periodicallyNode.Enabled -eq "true") { $kind = $periodicallyNode.Kind $fullPeriod = $periodicallyNode.FullPeriod if ($kind -eq "Hours") { $fullPeriod = $fullPeriod / 60; } $schTxt += "Every " + $fullPeriod + " " + $kind; } elseif ($continuousNode.enabled -ne "false") { $schTxt += "Continuous"; } elseif ($chainingNode.Enabled -eq "true") { $jobId = $chainingNode.PreviousJobId schTxt += "After [" + $jobId + "]"; } return $schTxt }
