$JobsByType = ($Jobs | ?{$_.Type -eq "51"}) # Type 51 - File Copy Job $Count = $JobsByType.Length IF ($DebugLogging.ToUpper() -eq "TRUE") {$momapi.LogScriptEvent($ScriptName,$EventID,0,"`nJob count is $($Count)")}
